import './BehaviorDirect.css';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const BehaviorDirect = () => {
    const navigate = useNavigate();
    const [file, setFile] = useState(null); // 파일 상태 및 설정 함수
    const [uploadResult, setUploadResult] = useState(null); // 업로드 결과 상태 및 설정 함수

    const handletitleClick = () => {    
        navigate('/Home');
    };

    const handleFileChange = (event) => {
        setFile(event.target.files[0]); // 파일 상태 업데이트
    };

    const handleUpload = async () => {
        if (!file) {
            alert("파일을 선택해주세요.");
            return;
        }

        const formData = new FormData();
        formData.append("file", file);
        console.log("Uploading file:", file);
        try {
            const response = await fetch('http://127.0.0.1:5000/api/upload', {
                method: 'POST',
                body: formData,
            });
            if (response.ok) {
                const result = await response.json();
                console.log("업로드 성공: 결과 =", result);
                setUploadResult(result); // 업로드 결과 상태 업데이트
            } else {
                alert("업로드 실패");
            }
        } catch (error) {
            console.error("업로드 중 오류 발생", error);
        }
    };

    return (
        <div>
            <header> <img onClick={handletitleClick} alt="dog" src="img/header.png" /></header>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload}>Upload</button>

            {uploadResult && (
                <div>
                    <p>업로드 결과: {uploadResult.message}</p>
                </div>
            )}
        </div>
    );
};

export default BehaviorDirect;
